import React, { useEffect, useRef } from "react";
import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

const Explore = () => {
  const sectionRef = useRef(null);
  const greenRef = useRef(null);
  const redRef = useRef(null);
  const blueRef = useRef(null);

  useEffect(() => {
    let Elem = sectionRef.current;
    let greenElem = greenRef.current;
    let redElem = redRef.current;
    let blueElem = blueRef.current;

    let trigger = ScrollTrigger.create({
      trigger: Elem,
      start: "top top",
      pin: true,
      pinSpacing: false,
      //   markers: true,
    });

    let tl = gsap.timeline({
      scrollTrigger: {
        trigger: Elem,
        start: "top top",
        end: "bottom top",
        scrub: true,
        markers: true,
      },
    });

    tl.add(() => {
      gsap.to(greenElem, {
        xPercent: 200,
        ease: "power1.out",
      });
      gsap.to(redElem, {
        xPercent: 0,
        ease: "power1.out",
      });
      gsap.to(blueElem, {
        xPercent: 100,
        ease: "power1.out",
      });
    });

    return () => {
      if (trigger) trigger.kill();
    //   if (tl) tl.kill();
    };
  }, []);

  return (
    <div>
      <div className="black-space" ref={sectionRef}>
        {/* <p
          style={{
            color: "yellow",
            margin: "auto",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            fontSize: 30,
            height: "100%",
          }}
        >
          Hello
        </p> */}
      </div>
      <div className="explore">
        <div
          className="green"
          style={{ width: "33.33333%" }}
          ref={greenRef}
        ></div>
        <div
          className="blue"
          style={{ width: "33.33333%" }}
          ref={blueRef}
        ></div>
        <div className="red" style={{ width: "33.33333%" }} ref={redRef}></div>
      </div>
    </div>
  );
};

export default Explore;
