import React, { useEffect, useRef } from "react";
import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

const Explore = () => {
  const sectionRef = useRef(null);
  const greenRef = useRef(null);
  const redRef = useRef(null);
  const blueRef = useRef(null);

  useEffect(() => {
    const Elem = sectionRef.current;
    const greenElem = greenRef.current;
    const redElem = redRef.current;
    const blueElem = blueRef.current;

    gsap.to(greenElem, {
      y: "100%",
      duration: 1,
      ease: "power2.out",
      scrollTrigger: {
        trigger: Elem,
        start: "top top",
        end: "bottom top",
        scrub: 1, // Smoothly animates the elements based on scrolling
        markers: true,
        pin: true, // Pin the element while scrolling
        pinSpacing: false,
        onLeaveBack: () => {
          // This function triggers when leaving the pinned section
          gsap.to([greenElem, blueElem, redElem], {
            y: 0, // Reset elements to original position
            stagger: 0.1, // Stagger the animation
          });
        },
      },
    });

    return () => {
      ScrollTrigger.getAll().forEach((trigger) => trigger.kill()); // Kill all ScrollTriggers
    };
  }, []);

  return (
    <div>
      <div className="black-space" ref={sectionRef}>
        {/* Content */}
      </div>
      <div className="explore">
        <div
          className="green"
          style={{ width: "33.33333%" }}
          ref={greenRef}
        ></div>
        <div
          className="blue"
          style={{ width: "33.33333%" }}
          ref={blueRef}
        ></div>
        <div className="red" style={{ width: "33.33333%" }} ref={redRef}></div>
      </div>
    </div>
  );
};

export default Explore;
